model.css:
.obscured { display: none }
.unobscured { display: none }

.card {
 font-family: arial;
 font-size: 20px;
 text-align: center;
}
.card.night_mode {
 color: white;
 background-color: black;
}
.synonyms { font-size: .75em }
.pos { font-size: .75em; display: block }
.pos.m { color: #268bd2 }
.pos.f, .pos.f-el { color: #d33682 }
.pos.mf { color: #6c71c4 }
.pos.v { color: #b58900 }
.pos.v.reflexive { color: #cb4b16 }
.pos.adj { color: #2aa198 }
.pos.adv { color: #859900 }
.pos.interj { color: #dc322f }
.eng { font-style: italic; font-size: .75em; color: #839496 }

/* Hide synonyms that aren't in the deck */
/* .synonyms .extra { display: none } */

